(function executeRule(current, previous /*null when async*/ ) {
    const utmApi = new x_cadso_core.UTMsApi();
    const utmMappings = [];
	const name = current.getValue('name');
	const order = current.getValue('order');
	const table = current.getValue('table');
	const template = current.getValue('template');
	const locked = current.getValue('locked');
	const dynamicValue = current.getValue('dynamic_value');
	const utm = current.getUniqueValue();
	
    var utmMappingGr = new GlideRecordSecure('x_cadso_core_utm_mapping');
	utmMappingGr.addQuery('template', current.getValue('template'));
    utmMappingGr.query();

    while (utmMappingGr.next()) {
		const type = utmMappingGr.getValue('type');
		const recordGr = utmMappingGr.record.getRefRecord();
		
		if (!recordGr.isValidRecord()) continue;

		const canCreateMappingOptions = utmApi.canCreateMappingOptions({ recordGr, type });

		if (!canCreateMappingOptions) continue;

        utmMappings.push({
            mapping: utmMappingGr.getUniqueValue(),
			table,
			record: utmMappingGr.getValue('record'),
			template,
			utm,
			name,
			order,
			locked,
			dynamicValue
        });
    }

    const utmOptionGr = new GlideRecordSecure('x_cadso_core_utm_mapping_option');
    for (var x = 0; x < utmMappings.length; x++) {
		const utmMappining = utmMappings[x];
        utmApi.createMappingOption({
			tableGr: utmOptionGr,
            data: utmMappining
        });
    }

})(current, previous);

