(function executeRule(current, previous /*null when async*/) {

	var utmTemplateGr = new GlideRecordSecure('x_cadso_core_utm_template');
    utmTemplateGr.addActiveQuery();
    utmTemplateGr.query();
    const utmTemplates = [];

    while (utmTemplateGr.next()) {
        utmTemplates.push(utmTemplateGr.getUniqueValue());
    }

    const utm = current.getUniqueValue();
    const utmOptionGr = new GlideRecordSecure('x_cadso_core_utm_option');
    for (var x = 0; x < utmTemplates.length; x++) {
        utmOptionGr.initialize();
        utmOptionGr.newRecord();
        utmOptionGr.setValue('utm', utm);
        utmOptionGr.setValue('template', utmTemplates[x]);
        utmOptionGr.insert();
    }

})(current, previous);

