(function executeRule(current, previous /*null when async*/ ) {
    const template = current.getValue('template');
    var utmGr = new GlideRecordSecure('x_cadso_core_utm_option');
    utmGr.addQuery('template', template);
    utmGr.addActiveQuery();
    utmGr.query();
    const utms = [];

    while (utmGr.next()) {
        const locked = utmGr.getValue('locked');
        const dynamicValue = utmGr.getValue('dynamic_value');
        utms.push({
            sysId: utmGr.getUniqueValue(),
            name: utmGr.getValue('name'),
            order: utmGr.getValue('order'),
            locked,
            dynamicValue
        });
    }

    const utmApi = new x_cadso_core.UTMsApi();
    const utmOptionGr = new GlideRecordSecure('x_cadso_core_utm_mapping_option');
    for (var x = 0; x < utms.length; x++) {
        const utm = utms[x];
        utmApi.createMappingOption({
            tableGr: utmOptionGr,
            data: {
                utm: utm.sysId,
                template: template,
                mapping: current.getUniqueValue(),
                table: current.getValue('table'),
                record: current.getValue('record'),
                order: utm.order,
                name: utm.name,
                locked: utm.locked,
                dynamicValue: utm.dynamicValue
            }
        });
    }
})(current, previous);

