(function executeRule(current, previous /*null when async*/ ) {

    var utmGr = new GlideRecordSecure('x_cadso_core_utm');
    utmGr.addActiveQuery();
    utmGr.addQuery('default', true);
    utmGr.query();
    const utms = [];

    while (utmGr.next()) {
        utms.push(utmGr.getUniqueValue());
    }

    const template = current.getUniqueValue();
    const utmOptionGr = new GlideRecordSecure('x_cadso_core_utm_option');
    for (var x = 0; x < utms.length; x++) {
        utmOptionGr.initialize();
        utmOptionGr.newRecord();
        utmOptionGr.setValue('utm', utms[x]);
        utmOptionGr.setValue('template', template);
        utmOptionGr.insert();
    }

})(current, previous);

