(function executeRule(current, previous /*null when async*/ ) {

    let data = {};
    let contactDetailUtil = new x_cadso_core.ContactDetailUtilMS();
    let contactDetailID;
    let emailAddressNeeded = false;
    let phoneNumberNeeded = false;

    if (current.getValue('email')) {
        let emailGQ = contactDetailUtil.getContactDetailFromEmailAddress(current.getValue('email'));
        if (emailGQ) {
            data.x_cadso_core_email_address = emailGQ.sys_id;
            data.x_cadso_automate_contact_detail = emailGQ.contact_detail;
        } else {
            emailAddressNeeded = true;
        }
    }

    if (current.getValue('mobile_phone')) {
        phoneGQ = contactDetailUtil.getContactDetailFromPhoneNumber(current.getValue('phone_number'))
        if (phoneGQ) {
            data.x_cadso_core_phone_number = phoneGQ.sys_id;
            if (!data.x_cadso_automate_contact_detail) {
                data.x_cadso_automate_contact_detail = phoneGQ.contact_detail;
            }
        } else {
            phoneNumberNeeded = true;
        }
    }

    if (!data.x_cadso_automate_contact_detail) {
        let contactDetailGQ = contactDetailUtil.createContactDetail({
            contact: current.getUniqueValue()
        })
        data.x_cadso_automate_contact_detail = contactDetailGQ.sys_id;
    }

	if(emailAddressNeeded){
		let emailGR = new GlideRecord('x_cadso_core_email_address');
		emailGR.setValue('contact_detail', data.x_cadso_automate_contact_detail);
		emailGR.setValue('email', current.getValue('email'));
		let emailID = emailGR.insert();
		data.x_cadso_core_email_address = emailID;
	}

	if(phoneNumberNeeded){
		let phoneGR = new GlideRecord('x_cadso_core_phone_number');
		phoneGR.setValue('phone_number', current.getValue('mobile_phone'));
		phoneGR.setValue('contact_detail', data.x_cadso_automate_contact_detail);
		let phoneID = phoneGR.insert();
		data.x_cadso_core_phone_number = phoneID;
	}

    current.x_cadso_core_data = JSON.stringify(data);

})(current, previous);

