(function executeRule(current, previous /*null when async*/) {
	const type = current.getValue('type');
	const tableGr = current.record.getRefRecord();
	if (!type) return;
	if (!tableGr.isValidRecord()) return;
	const utmApi = new x_cadso_core.UTMsApi();
	const field = utmApi.getUTMField({ type });
	const acceptableStates = utmApi.getAcceptableStates({ type });
	const state = tableGr.getValue('state');
	
	if (acceptableStates && acceptableStates.length && acceptableStates.split(',').includes(state) && field) {
		tableGr.setValue(field, current.getValue('parameters'));
		tableGr.update();
	}

})(current, previous);

