script.value:
(function executeRule(current, previous /*null when async*/ ) {

    var utmTemplateGr = new GlideRecord('x_cadso_core_utm_template');
    utmTemplateGr.addQuery('default', true);
    utmTemplateGr.addQuery('type', current.getValue('type'));
    utmTemplateGr.addActiveQuery();
    utmTemplateGr.addQuery('sys_id', '!=', current.getUniqueValue());
    utmTemplateGr.query();

    if (utmTemplateGr.hasNext()) {
        gs.addInfoMessage('Only one default template can be set per type.');
    }

    while (utmTemplateGr.next()) {
        utmTemplateGr.setValue('default', false);
        utmTemplateGr.update();
    }

})(current, previous);

