(function executeRule(current, previous /*null when async*/ ) {

    var utmOptionGr = new GlideRecordSecure('x_cadso_core_utm_option');
	utmOptionGr.addQuery('template', current.getValue('template'));
	utmOptionGr.addQuery('utm', current.getValue('utm'));
	utmOptionGr.addQuery('sys_id', '!=', current.getUniqueValue());
    utmOptionGr.query();

    if (utmOptionGr.next()) {
		gs.addErrorMessage('A UTM can only be associated with a template once.');
		current.setAbortAction(true);
    }

})(current, previous);

