(function executeRule(current, previous /*null when async*/ ) {

    var utmTemplateGr = new GlideRecord('x_cadso_core_utm_template');
    utmTemplateGr.addQuery('default', true);
    utmTemplateGr.addQuery('type', current.getValue('type'));
	utmTemplateGr.addActiveQuery();
    utmTemplateGr.addQuery('sys_id', '!=', current.getUniqueValue());
    utmTemplateGr.query();

    if (!utmTemplateGr.next()) {
		current.setValue('default', true);
		current.setValue('active', true);
		gs.addInfoMessage('Forcing this template to be the default and active for this type. There must be one default and active template per type.');
    }

})(current, previous);

