(function executeRule(current, previous /*null when async*/ ) {
    const utmOptionGr = current.utm.getRefRecord();

    if (!utmOptionGr.isValidRecord()) {
        gs.addErrorMessage('Invalid UTM Option record.');
        current.setAbortAction(true);
    }

    const utmGr = utmOptionGr.utm.getRefRecord();

    if (!utmGr.isValidRecord()) {
        gs.addErrorMessage('Invalid UTM record.');
        current.setAbortAction(true);
    }

    const isInsert = current.operation() === 'insert';
    const isDynamic = utmOptionGr.getDisplayValue('dynamic_value') === 'true';
    const isInsertAndDynamic = isDynamic && isInsert;
    const isChange = isDynamic && (current.record.changes() || current.table.changes());

    const name = utmGr.getValue('name') || '';
    const label = utmGr.getValue('label') || '';
    const locked = utmGr.getValue('locked') || '';
    let value = current.getValue('value') || '';
    if (isInsert) {
        value = value || utmOptionGr.getValue('value') || utmGr.getValue('value') || '';
    }
    const utmsApi = new x_cadso_core.UTMsApi();
    if (isInsertAndDynamic || isChange) {
        value = utmsApi.getRecordValue({
            value,
            sysId: current.getValue('record'),
            table: current.getValue('table'),
            field: utmOptionGr.getValue('field')
        });
    } else {
        value = utmsApi.formatValue({
            value
        });
    }

    current.setValue('value', value);
    current.setValue('name', name);
    current.setValue('label', label);
    current.setValue('locked', locked);
})(current, previous);

