(function executeRule(current, previous /*null when async*/ ) {

    if (!current.mapping.nil()) {
		const currentMapping = current.mapping.getRefRecord();
		if (currentMapping.isValidRecord()) {
			const currentUTMs = getAllUTMs(currentMapping.getUniqueValue());
			currentMapping.setValue('utms', currentUTMs);
			currentMapping.update();
		}
    }

    function getAllUTMs(mapping) {
        var utmOptionGr = new GlideRecordSecure('x_cadso_core_utm_mapping_option');
        utmOptionGr.addQuery('mapping', mapping);
		utmOptionGr.orderBy('utm.order');
		utmOptionGr.orderBy('utm');
        utmOptionGr.query();
        const utms = [];
        while (utmOptionGr.next()) {
            utms.push(utmOptionGr.getUniqueValue());
        }
        return utms.join(',');
    }
})(current, previous);
