(function executeRule(current, previous /*null when async*/ ) {

    if (!current.template.nil()) {
		const currentTemplateGr = current.template.getRefRecord();
		if (currentTemplateGr.isValidRecord()) {
			const currentUTMs = getAllUTMs(currentTemplateGr.getUniqueValue());
			currentTemplateGr.setValue('utms', currentUTMs);
			currentTemplateGr.update();
		}
    }

    function getAllUTMs(template) {
        var utmOptionGr = new GlideRecordSecure('x_cadso_core_utm_option');
        utmOptionGr.addQuery('template', template);
		utmOptionGr.orderBy('order');
		utmOptionGr.orderBy('utm');
        utmOptionGr.query();
        const utms = [];
        while (utmOptionGr.next()) {
            utms.push(utmOptionGr.getUniqueValue());
        }
        return utms.join(',');
    }
})(current, previous);

