(function executeRule(current, previous /*null when async*/) {

	const color = current.getValue('color');
	const background = current.getValue('background');
	const activeColor = current.getValue('color_active');
	const hover = current.getValue('hover');

	const colorTableGr = new GlideRecordSecure('x_cadso_core_color');
	colorTableGr.addQuery('color', color);
	colorTableGr.addQuery('background', background);
	colorTableGr.addQuery('color_active', activeColor);
	colorTableGr.addQuery('hover', hover);

	if (!current.isNewRecord()) {
		colorTableGr.addQuery('sys_id', '!=', current.getUniqueValue());
	}
	
	colorTableGr.query();

	if (colorTableGr.next()) {
		current.setAbortAction(true);
		gs.info('This color combination already exists on Core Color table.');
		gs.addErrorMessage('This color combination already exists.');
	}

})(current, previous);

