(function executeRule(current, previous /*null when async*/ ) {

    var categoryGr = new GlideRecord('x_cadso_core_category');
    categoryGr.addQuery('name', current.getValue('name'));
    categoryGr.addQuery('sys_id', '!=', current.getUniqueValue());
	categoryGr.setLimit(1);
    categoryGr.query();

    while (categoryGr.next()) {
		gs.addErrorMessage('Name is already in use on the record: ' + current.getDisplayValue());
		current.setAbortAction(true);
    }

})(current, previous);

