(function executeRule(current, previous /*null when async*/ ) {
    const category = current.getValue('category');
    const subcategory = current.getValue('subcategory');
    var settingGr = new GlideRecord('x_cadso_core_setting');
    settingGr.addQuery('name', current.getValue('name'));
    settingGr.addQuery('sys_id', '!=', current.getUniqueValue());
    if (category) {
        settingGr.addQuery('category', category);
        if (subcategory) {
            settingGr.addQuery('subcategory', subcategory);
        }
    } else {
        settingGr.addEncodedQuery('categoryISEMPTY');
        if (subcategory) {
			// Should not happen but incase it does.
            settingGr.addQuery('subcategory', subcategory);
        } else {
            settingGr.addEncodedQuery('subcategoryISEMPTY');
        }
    }
    settingGr.setLimit(1);
    settingGr.query();

    while (settingGr.next()) {
        gs.addErrorMessage('Name is already in use on the record: ' + current.getDisplayValue());
        gs.addErrorMessage('Name must be unique to the category / subcategory combination');
        current.setAbortAction(true);
    }

})(current, previous);

