(function executeRule(current, previous /*null when async*/) {

	let userFields = [];
	let settingsGQ = new global.GlideQuery('x_cadso_core_setting')
	.where('category', '9d8992d833492a507b18bc534d5c7b70')
	.where('subcategory', '011aded833492a507b18bc534d5c7b25')
	.orderBy('value')
	.select('name')
	.forEach(setting => userFields.push(setting.name));

	let fieldsToSync = ['first_name', 'last_name', 'email', 'mobile_phone', 'company']

	let contactDetailGR = new GlideRecord('x_cadso_automate_contact_detail');
	contactDetailGR.addQuery('lead', current.getUniqueValue());
	contactDetailGR.query();
	if(!contactDetailGR.next()){
		return;
	}

	for(let field of fieldsToSync){
		for(let userField of userFields){
			let recordGR = contactDetailGR[userField].getRefRecord();
			if(!recordGR || !recordGR.isValidRecord()){
				continue;
			}
			let value = recordGR.getValue(field);
			if(value){
				contactDetailGR.setValue(field, value);
				break;
			}
		}
	}
	contactDetailGR.update();



	

})(current, previous);

