(function executeRule(current, previous /*null when async*/) {

	const utmOptionGr = new GlideRecordSecure('x_cadso_core_utm_option');
	utmOptionGr.addQuery('utm', current.getUniqueValue());
	utmOptionGr.query();
	while(utmOptionGr.next()) {
		utmOptionGr.setValue('name', current.getDisplayValue('name'));
		utmOptionGr.setValue('order', current.getDisplayValue('order'));
		utmOptionGr.update();
	}

})(current, previous);

