(function executeRule(current, previous /*null when async*/) {

	const utmMappingOptionGr = new GlideRecordSecure('x_cadso_core_utm_mapping_option');
	utmMappingOptionGr.addQuery('utm', current.getUniqueValue());
	utmMappingOptionGr.query();
	while(utmMappingOptionGr.next()) {
		utmMappingOptionGr.setValue('name', current.getDisplayValue('name'));
		utmMappingOptionGr.setValue('locked', current.getValue('locked'));
		utmMappingOptionGr.setValue('dynamic_value', current.getValue('dynamic_value'));
		utmMappingOptionGr.update();
	}

})(current, previous);

