(function executeRule(current, previous /*null when async*/ ) {
	const bucketGr = current.bucket.getRefRecord();
	if (!bucketGr.isValidRecord()) return;
    var bucket = (bucketGr.getDisplayValue('name') + '').trim();
    var slug = slugify(current.getValue('slug'));

    if (bucket.endsWith('/')) {
        bucket = bucket.slice(0, -1);
    }
	
    current.setValue('url', bucket + '/' + slug);

    function slugify(text) {
        return text.toString().trim().toLowerCase()
            .replace(/[^\w\s-]/g, '')
            .replace(/[\s_-]+/g, '-')
            .replace(/^-+|-+$/g, '');
    }

})(current, previous);

