(function executeRule(current, previous /*null when async*/) {

    var landingPageGR = new GlideRecord('x_cadso_cloud_page');
    landingPageGR.addQuery('active', true);
    landingPageGR.addQuery('slug', current.getValue('slug'));
    landingPageGR.addQuery('bucket', current.getValue('bucket'));
	
    if (!current.isNewRecord()) {
        landingPageGR.addQuery('sys_id', '!=', current.getUniqueValue());
    }

    landingPageGR.query();

    if (landingPageGR.next()) {
        gs.addErrorMessage("An active record with the same URL already exists: " + current.getValue('url'));
        current.setAbortAction(true);
    }

})(current, previous);

