script.value:
(function executeRule(current, previous) {
    const state = current.getValue('state');
    let status = '';
    if (!current.state.nil()) {
        var statusGr = new GlideRecord('x_cadso_core_status');
        statusGr.addQuery('table', 'x_cadso_cloud_page');
        statusGr.addQuery('state_value', state);
        statusGr.addQuery('active', 'true');
		statusGr.setLimit(1);
		statusGr.orderByDesc('sys_updated_on');
        statusGr.query();
        while (statusGr.next()) {
			status = statusGr.getUniqueValue();
        }
    }
    current.setValue('status', status);
})(current, previous);

