(function executeRule(current, previous /*null when async*/ ) {

    const updatedByUsername = current.getValue('sys_updated_by');
    let updatedBy = '';
    var sysUserGr = new GlideRecord('sys_user');
    sysUserGr.addQuery('user_name', updatedByUsername);
    sysUserGr.query();

    while (sysUserGr.next()) {
        updatedBy = sysUserGr.getUniqueValue();
    }

    const startDate = previous.x_cadso_ti_agile_last_state_change.getGlideObject();
    const endDate = current.x_cadso_ti_agile_last_state_change.getGlideObject();
    const schedule = new GlideSchedule();
    schedule.load('08fcd0830a0a0b2600079f56b1adb9ae');
    const duration = schedule.duration(startDate, endDate);

    const reasonGr = new GlideRecord('x_cadso_ti_agile_state_duration');
    reasonGr.initialize();
    reasonGr.newRecord();
    reasonGr.setValue('story', current.getUniqueValue());
    reasonGr.setValue('assignee', previous.getValue('assigned_to'));
    reasonGr.setValue('updated_by', updatedBy);
    reasonGr.setValue('new_state', current.getValue('state'));
    reasonGr.setValue('previous_state', previous.getValue('state'));
    reasonGr.setValue('duration', duration);
	reasonGr.setValue('start_date', startDate);
	reasonGr.setValue('end_date', endDate);
    reasonGr.insert();

})(current, previous);

