(function executeRule(current, previous /*null when async*/ ) {
    function getLeadRatings() {
        let minRating = 0;
        let lowestRating = 0;
        let leadRatings = [];
        let ratingGR = new GlideRecord('x_cadso_lead_rating_definition');
        ratingGR.orderBy('max_rating');
        ratingGR.query();

        while (ratingGR.next()) {
            let maxRating = Number(ratingGR.getValue('max_rating'));
            if (maxRating < 0) {
                gs.info("LEAD SCORING ERROR: RATING SCORE WAS LESS THEN 0. THE RATING WAS CHANGED TO 0");
                maxRating = 0;
            }
            let newRating = ratingGR.segment_name.value.getValue();
            let newAction = ratingGR.getValue('marketing_sales_action');
            leadRatings.push({
                scoreRange: [minRating, maxRating],
                rating: newRating,
                action: newAction
            });
            minRating = maxRating;
        }
        return leadRatings;
    }
    try {
        // Get form values
        const firstName = current.first_name.getValue();
        const lastName = current.last_name.getValue();
        const email = current.email.getValue();
        const company = current.company.getValue();
        const title = current.title.getValue();
        const phone = current.business_phone.getValue();
        const mobilePhone = current.mobile_phone.getValue();
        const description = current.short_description.getValue();
        let message = "";
        let count = 1;
        let totalScore = Number(current.lead_score.getValue());
        const leadRatings = getLeadRatings();



        if (email) {
            totalScore += 2;
            message += count + ". User provided an email address. 2 points added to the lead score.\n";
            count++;
        }
        if (company) {
            totalScore += 2;
            message += count + ". User provided a company name. 2 points added to the lead score.\n";
            count++;
        }
        if (title) {
            totalScore += 2;
            message += count + ". User provided a title. 2 points added to the lead score.\n";
            count++;
        }
        if (phone || mobilePhone) {
            totalScore += 3;
            message += count + ". User provided a phone number. 3 points added to the lead score.\n";
            count++;
        }
        if (firstName || lastName) {
            totalScore += 1;
            message += count + ". User provided a name. 1 point added to the lead score.\n";
            count++;
        }
        if (description) {
            totalScore += 2;
            message += count + ". User provided a description. 2 points added to the lead score.\n";
            count++;
        }

        if (totalScore > 0) {
            message += "\n\nThe sales score for this user has been updated to " + totalScore + ".";
        }
        let highestRating = 0;
        let tempScore = totalScore;
        for (let r = 0; r < leadRatings.length; r++) {

            if (highestRating < leadRatings[r].scoreRange[1]) {
                highestRating = leadRatings[r].scoreRange[1];
            }
        }

        for (let w = 0; w < leadRatings.length; w++) {
            let minRating = leadRatings[w].scoreRange[0];
            let maxRating = leadRatings[w].scoreRange[1];

            if (tempScore > highestRating) {
                tempScore = highestRating;
            }

            if (tempScore <= 0) {
                tempScore = 1;
            }
            if (tempScore > minRating && tempScore <= maxRating) {
                current.lead_rating = leadRatings[w].rating;
                current.x_cadso_lead_marketing_sales_action = leadRatings[w].action;
                break;
            }
        }
        current.lead_score = totalScore;
        current.x_cadso_lead_sales_score = totalScore;
        current.work_notes = message;
    } catch (e) {
        gs.info("Lead Create Scoring Business Rule Error: " + e.message);
    }
})(current, previous);

