script.value:
(function executeRule(current, previous /*null when async*/ ) {

    var segment = current.getValue('segment_name');
    var prevSegment = previous.getValue('segment_name');
    var segmentName = current.getDisplayValue('segment_name');
    var segmentChange = prevSegment !== segment;
    var segmentRef = current.segment_name.getRefRecord();
    var isValidUpdate = segmentRef.isValidRecord();

	if(!isValidUpdate){
		current.setAbortAction(true);
        gs.addErrorMessage('A Invalid Segment was used for new Lead Rating Definition: ' + segmentName);
	}

    if (segmentChange) {
        var ratingDefGR = new GlideRecord('x_cadso_lead_rating_definition');
        ratingDefGR.addQuery('segment_name', segment);
        ratingDefGR.query();

        //If another record already has a the segment, abort insert or update.
        if (ratingDefGR.next()) {
            current.setAbortAction(true);
            gs.addErrorMessage('A Lead Rating Definition already exists for the Segment: ' + segmentName);
        }
    }

})(current, previous);

