(function executeRule(current, previous /*null when async*/ ) {
    //Check core if tenon lead scoring is enabled
    var scoringEnabled = false;
	var settingGR = new GlideRecord('x_cadso_core_setting');
	settingGR.addQuery('name', 'x_cadso_lead.tenon_marketing_lead_scoring');
	settingGR.addQuery('active', 'true');
	settingGR.query();
	if(settingGR.next()){
		scoringEnabled = settingGR.getValue('value');
	}
	
    if (scoringEnabled == 'true') {
        //Check which features are installed
        var featureAPI = new x_cadso_core.FeatureAvailability();
        var leadEnabled = featureAPI.isLeadEnabled();
        var contactEnabled = featureAPI.isContactEnabled();
        var consumerEnabled = featureAPI.isConsumerEnabled();
        var accountEnabled = featureAPI.isAccountEnabled();
		
        //If any features are enabled, check contact detail for consumer, lead, or contacts
        if (leadEnabled || contactEnabled || consumerEnabled || accountEnabled) {
            var scoreAPI = new LeadScoring();
            var status = current.status;
            var messageType = current.recipient_type;
            var sysId = current.sys_id;
            var eventType = 'email';
            if (messageType == 'sms_mms') {
                eventType = 'message';
            }
            var score = scoreAPI.getScore(status, eventType);

            var leadEventsGR = new GlideRecord('x_cadso_lead_event');
            leadEventsGR.initialize();
            leadEventsGR.newRecord();
            leadEventsGR.setValue('score', score);
            if (leadEnabled && current.contact_detail.lead) {
                var lead = current.contact_detail.lead.getRefRecord();
                if (lead.isValidRecord()) {
					var leadId = lead.getValue('sys_id');
                    leadEventsGR.setValue('lead', leadId);
                }
            }
            if (contactEnabled && current.contact_detail.contact) {
                var contact = current.contact_detail.contact.getRefRecord();
				if(contact.isValidRecord()){
					var contactId = contact.getValue('sys_id');
					leadEventsGR.setValue('contact', contactId);
				}
            }
            if (consumerEnabled && current.contact_detail.consumer) {
                var consumer = current.contact_detail.consumer.getRefRecord();
				if(consumer.isValidRecord()){
					var consumerId = consumer.getValue('sys_id');
					leadEventsGR.setValue('consumer', consumerId);
				}
            }
            if (accountEnabled && current.contact_detail.account) {
                var account = current.contact_detail.account.getRefRecord();
                if (account.isValidRecord()) {
					var accountId = account.getValue('sys_id');
                    leadEventsGR.setValue('account', accountId);
                }

            }
            leadEventsGR.setValue('contact_consent', sysId);
            leadEventsGR.setValue('event', status);
            leadEventsGR.setValue('event_type', eventType);
            leadEventsGR.insert();
        }
    }


})(current, previous);

