script.value:
(function executeRule(current, previous /*null when async*/) {

    var htmlContent = current.email_html;

	var newPreheader = '<div class="preheader" style="font-size:1px;line-height:1px;display:none;color:#001232;max-height:0;max-width:0;opacity:0;overflow:hidden">' + current.preview_text + '</div>';

    var bodyTagStart = htmlContent.indexOf('<body');
    var classBodyStart = htmlContent.indexOf('class="body', bodyTagStart);
    var bodyTagEnd = -1;
    
    if (classBodyStart !== -1) {
        bodyTagEnd = htmlContent.indexOf('>', classBodyStart);
    }

	var preheaderStart = htmlContent.indexOf('<div class="preheader"', bodyTagEnd);

    if (bodyTagEnd !== -1) {
		if (preheaderStart !== -1) {
            // Preheader exists, update it
            var preheaderEnd = htmlContent.indexOf('</div>', preheaderStart) + 6;
            var beforePreheader = htmlContent.substring(0, preheaderStart);
            var afterPreheader = htmlContent.substring(preheaderEnd); 

            htmlContent = beforePreheader + newPreheader + afterPreheader;

		} else {
            var beforeBodyTag = htmlContent.substring(0, bodyTagEnd + 1); 
            var afterBodyTag = htmlContent.substring(bodyTagEnd + 1);
            htmlContent = beforeBodyTag + newPreheader + afterBodyTag;
        }

		current.email_html = htmlContent;
	}

})(current, previous);

