(function executeRule(current, previous /*null when async*/ ) {
    // Grab the sys_id of the customer central module
    var ccModule = gs.getProperty('sn_customercentral.module_sys_id');
	var actSubCtx = global.ActivitySubscriptionContext.getContext(); 

    if (current.contact_detail.contact) {
        actSubCtx.getActivityService().createActivity(current, "sms_contact_event", "", "", "", ccModule);
    }

    if (current.contact_detail.account) {
        actSubCtx.getActivityService().createActivity(current, "sms_account_event", "", "", "", ccModule);
    }

    if (current.contact_detail.account) {
        actSubCtx.getActivityService().createActivity(current, "sms_consumer_event", "", "", "", ccModule);
    }

})(current, previous);

