script.value:
(function executeRule(current, previous /*null when async*/ ) {
	const sendName = current.getDisplayValue('name');
	const sendType = current.getValue('automate_type');
    const messageSendGr = new GlideRecord('x_cadso_automate_message_send');
    messageSendGr.initialize();
    messageSendGr.newRecord();
    messageSendGr.setValue('message', current.getUniqueValue());
	messageSendGr.setValue('name', sendName);
	messageSendGr.setValue('status','draft');
    messageSendGr.setValue('send_now', true); //default to send now
	messageSendGr.setValue('type', sendType);
    messageSendGr.insert();

})(current, previous);

