(function executeRule(current, previous /*null when async*/) {
	try {
		const msg_obj = JSON.parse(current.rich_text_object);
		const label = msg_obj.attachments[0].label;
		const b64contentRegex = /;base64,(.*)/;
		const b64contentTypeRegex = /data:([^;]*)/;
		const b64content = msg_obj.attachments[0].value.match(b64contentRegex);
		const b64contentType = msg_obj.attachments[0].value.match(b64contentTypeRegex);

		if (b64content && b64contentType) {
			var attachment = new GlideSysAttachment();
			var attId = attachment.writeBase64(current, label, b64contentType[1], b64content[1]);
			current.media = attId;
		} else {
			gs.warn("Create attachment for media BR triggered, but was unsucessful");
		}
	} catch (error) {
		gs.error(error);
	}
})(current, previous);

