(function executeRule(current, previous /*null when async*/ ) {
    try {
		var audienceApi = new Audience;
		var tableFields = audienceApi.tableFields;
		var formatPhoneNumber = audienceApi.formatPhoneNumber;

		var table = current.getTableName();
        var sysId = current.sys_id || "";
        var first = current.first_name || "";
        var last = current.last_name || "";
        var phone = current.mobile_phone || "";
        var email = current.email || "";
        var name = current.name || "";
		var tableField = tableFields[table];
		var sanitizedPhone = phone.replace(/\D+/g, '');
		var formattedPhone = formatPhoneNumber(sanitizedPhone);

        var cdGR = new GlideRecord('x_cadso_automate_contact_detail');
        cdGR.addQuery(tableField, sysId);
        cdGR.query();
        if (cdGR.next()) {
            cdGR.name = name;
            cdGR.phone_number = formattedPhone;
            cdGR.email_address = email;
            cdGR.update();
        } else {
            cdGR.initialize();
            cdGR.name = name;
            cdGR.phone_number = formattedPhone;
            cdGR.email_address = email;
            cdGR[tableField] = sysId;
            cdGR.table = table;
            cdGR.document_id = sysId;
            cdGR.insert();
        }
    } catch (e) {
        gs.info("User/Contact contact detail update BR error: " + e.message);
    }

})(current, previous);

