(function executeRule(current, previous /*null when async*/ ) {

	const type = current.getValue('type');
    const sendNow = current.getValue('send_now') === '1' || current.getValue('send_now') === 1;
    const hasDeliveryValue = !current.delivery_time.nil();
    const hasProfileValue = !current.message_number.nil();
    const hasAudience = !current.audiences.nil();
    const hasTextValue = !current.text.nil();
	const canSendAutomated = type === 'automated' && hasTextValue && hasProfileValue;
    const canSendNow = hasProfileValue && hasAudience && hasTextValue;
    const canSendFuture = canSendNow && hasDeliveryValue;

	
    let canSend = false;

    if (canSendNow && sendNow) {
        canSend = true;
    } else if (canSendFuture) {
        canSend = true;
    } else if (canSendAutomated){
		canSend = true;
	}

    current.setValue('can_send', canSend);
	
    let messageGr = current.message.getRefRecord();
    if (messageGr.isValidRecord()) {
		messageGr.setWorkflow(false);
        messageGr.can_send = canSend;
        messageGr.update();
		messageGr.setWorkflow(true);
    }


})(current, previous);

