(function executeRule(current, previous /*null when async*/ ) {

    const sendNow = current.getValue('send_now') === '1' || current.getValue('send_now') === 1;
    const hasDeliveryValue = !current.delivery_time.nil();
    const hasProfileValue = !current.from_address.nil();
    const hasAudience = !current.audiences.nil();
    const hasSubjectLine = !current.static_subject_line.nil() || !current.dynamic_subject_line.nil();
    const isAutomated = !current.type.nil() && current.getValue('type') === 'automated';
    const canSendNow = hasProfileValue && hasAudience && hasSubjectLine;
	const canSendAutomated = hasProfileValue && hasSubjectLine;
    const canSendFuture = canSendNow && hasDeliveryValue;

    let canSend = false;

    if (canSendNow && sendNow) {
        canSend = true;
    } else if (canSendFuture) {
        canSend = true;
    } else if (canSendAutomated && isAutomated) {
		canSend = true;
	}
	
    current.setValue('can_send', canSend);

    let emailGr = current.email.getRefRecord();
    if (emailGr.isValidRecord()) {
		emailGr.setWorkflow(false);
        emailGr.can_send = canSend;
        emailGr.update();
		emailGr.setWorkflow(true);
    }


})(current, previous);

