(function executeRule(current, previous /*null when async*/ ) {


    try {
        var AudienceMS = new x_cadso_automate.AudienceMS();
        AudienceMS.refreshAudienceHash({
            audienceSysID: current.getUniqueValue(),
			newConditions: current.getValue('conditions'),
            update: true
        });
    } catch (ex) {
        var message = ex.message;
        gs.error("Audience Members Error: " + message);
    }
	current.setValue('hash_state', 'active');
	current.setValue('members_state', 'generating');
	current.update();
})(current, previous);

