(function executeRule(current, previous /*null when async*/ ) {
    var audienceApi = new Audience;
    var tableFields = audienceApi.tableFields;
	var audienceFields = audienceApi.audienceTableFields;
	var formatPhoneNumber = audienceApi.formatPhoneNumber;
    try {
        var table = current.audience.table;
        var field = audienceFields[table];
		var insertField = tableFields[table];
		var sysId = current[field];
		var memberHash = JSON.parse(current[field].x_cadso_automate_contact_detail_data || "[]");
		var email = memberHash.email.member.address || "";
		var phone = memberHash.sms.phone || "";
		var name = memberHash.email.member.name || "";
		var formattedPhone = formatPhoneNumber(phone);

        var cdQuery = "table=" + table + "^document_id=" + sysId + "^" + insertField + "=" + sysId;
        var matchFound = false;
		var cdId = "";

        var cdGR = new GlideRecord('x_cadso_automate_contact_detail');
        cdGR.addEncodedQuery(cdQuery);
        cdGR.query();
        if (cdGR.next()) {
            matchFound = true;
			cdId = cdGR.getUniqueValue();
        }

        if (!matchFound) {
            var cdInsertGR = new GlideRecord('x_cadso_automate_contact_detail');
            cdInsertGR.initialize();
            cdInsertGR.table = table;
            cdInsertGR.document_id = sysId;
            cdInsertGR[insertField] = sysId;
			cdInsertGR.email_address = email;
			cdInsertGR.phone_number = formattedPhone || "";
			cdInsertGR.name = name;
            cdId = cdInsertGR.insert();
        }

		current.contact_detail = cdId;
		current.update();
    } catch (e) {
        gs.info("Create Contact Detail from Audience Member Error for Audience " + current.name + ": " + e.message );
    }


})(current, previous);

