(function executeRule(current, previous /*null when async*/) {
	
    let toAddress = current.getValue('to_address');
    
    let emailMatch = toAddress.match(/<([^>]+)>/);
    
    if (emailMatch && emailMatch[1]) {
        let emailAddress = emailMatch[1];
        
        let contactDetailGR = new GlideRecord('x_cadso_automate_contact_detail');
        if(contactDetailGR.get('email_address', emailAddress)){
            current.setValue('contact_detail', contactDetailGR.getUniqueValue());
        }
    }
})(current, previous);

