script.value:
(function executeRule(current, previous /*null when async*/) {

	if(current.default == true){
		const senderGR = new GlideRecord('x_cadso_automate_sender_email');
		//Find the record with the default value set to "true", exclude the current record
		senderGR.addQuery('default', true);
		senderGR.addQuery('sys_id' , '!=', current.sys_id);
		senderGR.query();
        //Set the default field
		senderGR.setWorkflow(false);
		while(senderGR.next()){
			senderGR.default = false;
			senderGR.update();
		}
		senderGR.setWorkflow(true);
	}

})(current, previous);

