script.value:
(function executeRule(current, previous /*null when async*/) {

	if(current.default == true){
		const skitGr = new GlideRecord('x_cadso_automate_style_kit');
		//Find the record with the default value set to "true", exclude the current record
		skitGr.addQuery('default', true);
		skitGr.addQuery('sys_id' , '!=', current.sys_id);
		skitGr.query();
        //Set the default field
		skitGr.setWorkflow(false);
		while(skitGr.next()){
			skitGr.default = false;
			skitGr.update();
		}
		skitGr.setWorkflow(true);
	}

})(current, previous);

