script.value:
(function executeRule(current, previous /*null when async*/) {
    
    // Check if the email reference field has a value
    if (current.email && !current.email.nil()) {
        
        // Get the referenced email record from x_cadso_automate_email table
        var emailGR = new GlideRecord('x_cadso_automate_email');
        
        if (emailGR.get(current.email)) {
            // Update the plain_text field using setValue for better handling
            current.setValue('plain_text', emailGR.getValue('plain_text'));

        } else {
            // Optional: Log if email record not found
            gs.warn('Email record not found in x_cadso_automate_email for sys_id: ' + current.email);
        }
    }
    
})(current, previous);

