(function executeRule(current, previous /*null when async*/ ) {

    if (!current.from_address.nil()) {
        const fromAddressGr = current.from_address.getRefRecord();
        if (fromAddressGr.isValidRecord()) {
            current.setValue('domain', fromAddressGr.getValue('domain'));
            current.setValue('reply_to_address', fromAddressGr.getValue('reply_to_address'));
            current.setValue('from_name', fromAddressGr.getValue('from_name'));
            return;
        }
    }

    current.setValue('domain', '');
    current.setValue('reply_to_address', '');
    current.setValue('from_name', '');

})(current, previous);

