(function executeRule(current, previous /*null when async*/) {

	const contactChanged = current.contact.changes() && !current.contact.nil();
	const userChanged = current.user.changes() && !current.user.nil();
	const leadChanged = current.lead.changes() && !current.lead.nil();
	const consumerChanged = current.consumer.changes() && !current.consumer.nil();
	const accountChanged = current.account.changes() && !current.account.nil();
	let table = '';
	let documentId = '';

	if (contactChanged) {
		table = 'customer_contact';
		documentId = current.getValue('contact');
	} else if (userChanged) {
		table = 'sys_user';
		documentId = current.getValue('user');
	} else if (leadChanged) {
		table = 'sn_le'+ 'ad_mgmt_core_le' + 'ad';
		documentId = current.getValue('lead');
	} else if (consumerChanged) {
		table = 'csm_consumer';
		documentId = current.getValue('consumer');
	} else if (accountChanged) {
		table = 'customer_account';
		documentId = current.getValue('account');
	}

	current.setValue('table', table);
	current.setValue('document_id', documentId);

})(current, previous);

