(function executeRule(current, previous /*null when async*/) {
	
    if (current.internal_type == 'collection') {
        return;
    }

    var targetTables = [
        'sn_lead_mgmt_core_lead',
        'customer_contact',
        'csm_consumer',
        'sys_user',
        'customer_account'
    ];

    if (!targetTables.includes(current.name + '')) {
        return;
    }

    var isCustomField = current.element.startsWith('u_') || current.element.startsWith('x_cadso_');

    var tag = new GlideRecord('x_cadso_core_merge_tag');
    tag.initialize();
    tag.field = current.element;
    tag.table = current.name;
    tag.name = current.column_label;
    tag.active = false;
    tag.custom = isCustomField;
    tag.insert();

})(current, previous);

