(function executeRule(current, previous /*null when async*/ ) {
    const messageStatusObj = {
        draft: ['draft', 'new'],
        scheduled: ['scheduled', 'send-ready'],
        queue: ['ready_to_process', 'processing', 'sending'],
        sent: ['completed'],
        error: ['error']
    };

    const status = current.getValue('status');
    const messageGR = current.message.getRefRecord();

    if (messageGR.isValidRecord()) {
        var relatedSendStatuses = [];
        var relatedSends = new GlideRecord('x_cadso_automate_message_send');
        relatedSends.addQuery('message', messageGR.sys_id);
        relatedSends.addQuery('sys_id', '!=', current.sys_id);
        relatedSends.query();
        while (relatedSends.next()) {
            relatedSendStatuses.push(relatedSends.getValue('status'));
        }

        var value = Object.keys(messageStatusObj).find(function(key) {
            const messageStatuses = messageStatusObj[key];
            return messageStatuses.includes(status);
        });


        // Parent message status is dependent on all related message send records.
        // If any other message sends for this message are errored, the parent message status should be error.
        if (relatedSendStatuses.includes('error')) {
            value = 'error';
        }
        // if only one message has been sent, and others are not yet sent, the parent message should not yet be set to completed.
        else if (value == 'sent' &&
            (relatedSendStatuses.includes('ready_to_process') ||
                relatedSendStatuses.includes('processing') ||
                relatedSendStatuses.includes('sending'))
        ) {
            value = 'queue';
        }

        messageGR.setValue('status', value);
        messageGR.update();
    }

})(current, previous);

