                break;
            case 'Contact':
                staticView = 'contact';
                view = 'auto_list';
                break;
            case 'Employee':
                staticView = 'employee';
                view = 'auto_list';
                break;
            case 'Company':
                staticView = 'company';
                view = 'auto_list';
                break;
			case 'Lead':
                staticView = 'lead';
                view = 'auto_list';
                break;
            default:
                staticView = 'auto_list';
                view = 'auto_list';
                break;
        }
    }
	
	current.setValue('view', view);
	current.setValue('static_view', staticView);

})(current, previous);

