(function executeRule(current, previous /*null when async*/) {
	let versionStep = current.version_step.getRefRecord();
	if (!versionStep.isValidRecord()) return;
	const datetime = versionStep.datetime.nil() ? "" : versionStep.getValue("datetime");

	let now = new GlideDateTime();
	let gdt = new GlideDateTime();
	gdt.setValue(datetime);
	const jbDateTime = new JourneyBuilderDateTime();
	const { glideDuration } = jbDateTime.getDateTimeDuration({ now, glideDatetime: gdt });
	current.setValue('duration', glideDuration);
	current.setValue('datetime', gdt);
})(current, previous);

