script.value:
(function executeRule(current, previous /*null when async*/ ) {
    const versionStepGr = current.version_step.getRefRecord();
    if (!versionStepGr.isValidRecord()) return;
	
    const targetDayOfWeek = versionStepGr.day_of_week.nil() ? 0 : Number(versionStepGr.getValue("day_of_week"));
    const time = versionStepGr.time.nil() ? "" : versionStepGr.getValue("time");
    const jbDateTime = new JourneyBuilderDateTime();
	const { glideDuration, glideDateTime } = jbDateTime.getNextDayOfWeekDateTime({
        time,
        dayOfTheWeek: targetDayOfWeek
    });

    current.setValue("duration", glideDuration);
    current.setValue("datetime", glideDateTime);
})(current, previous);

