(function executeRule(current, previous /*null when async*/ ) {
    let versionStep = current.version_step.getRefRecord();
    if (!versionStep.isValidRecord()) return;

    const time = versionStep.time.nil() ? "" : versionStep.getValue("time");

    const jbDateTime = new JourneyBuilderDateTime();
    const { glideDuration, glideDateTime, glideTime } = jbDateTime.getNextTimeDateTime({
        time
    });

    current.setValue('duration', glideDuration);
    current.setValue('time', glideTime);
    current.setValue('datetime', glideDateTime);
})(current, previous);

