(function executeRule(current, previous /*null when async*/ ) {

    let journeyGR = current.journey.getRefRecord();

    if (!journeyGR.isValidRecord()) return;

    let publishedVersions = new global.GlideQuery('x_cadso_journey_version')
        .where('journey', current.journey)
        .where('published', true)
        .where('sys_id', '!=', current.getUniqueValue())
        .count();

    if (publishedVersions === 0) {
        journeyGR.setValue('published', false);
        journeyGR.update();
    }

})(current, previous);

