(function executeRule(current, previous /*null when async*/ ) {
	// Intentionally using GlideRecord vs GlideRecordSecure here because...
	// We want the update made regardless of the current users permissions.
	// Classified: Business Logic

    GetUniqueArray = function(arr) {
        var uniqueArr = [...new Set(arr)];
        return uniqueArr;
    };

    //------------------------------------------ Find all the sysUsers tied to the group -----------------------------------------------------------------------------------------

    var sysUserArray = [];
    var groupMemberGR = new GlideRecord("sys_user_grmember");
	groupMemberGR.addQuery('group', current.getValue('group'));
    groupMemberGR.query();
    while (groupMemberGR.next()) {
		sysUserArray.push(groupMemberGR.getValue('user'));
    }
	
    sysUserArray = GetUniqueArray(sysUserArray);

    for (var x = 0; x < sysUserArray.length; x++) {
        if (sysUserArray[x] !== "") {
            var createSysUsertoWorkGroupGR = new GlideRecord("x_cadso_work_group_sys_user_m2m");
            createSysUsertoWorkGroupGR.initialize();
			createSysUsertoWorkGroupGR.setValue('user', sysUserArray[x].toString());
			createSysUsertoWorkGroupGR.setValue('work_group', current.getValue('work_group'));
			createSysUsertoWorkGroupGR.setValue('inherit', true);
			createSysUsertoWorkGroupGR.setValue('granted_by', current.getValue('group'));
            createSysUsertoWorkGroupGR.insert();
        }
    }

})(current, previous);

