script.value:
(function executeRule(current, previous /*null when async*/ ) {
	// Intentionally using GlideRecord vs GlideRecordSecure here because...
	// We want the update made regardless of the current users permissions.
	// Classified: Business Logic
	
    GetUniqueArray = function(arr) {
        var uniqueArr = [...new Set(arr)];
        return uniqueArr;
    };

    //------------------------------------------ Find all the Work Groups that are tied to the current group-------------------------------------------------------

    var workGroup_Array = "";
    var workGroupGR = new GlideRecord("x_cadso_work_group_to_group_m2m");
    workGroupGR.addQuery("group", current.getValue('group'));
    workGroupGR.query();
    while (workGroupGR.next()) {
        workGroup_Array += workGroupGR.getValue('work_group') + ',';
    }
    var temp = workGroup_Array.split(',');
    workGroup_Array = GetUniqueArray(temp);

    //------------------------------------------ Create a SysUser to WorkGroup m2m record for each WorkGroup ---------------------------------------------------

    for (var x = 0; x < workGroup_Array.length; x++) {
        if (workGroup_Array[x] !== "") {
            var workGroupToSysUserGR = new GlideRecord("x_cadso_work_group_sys_user_m2m");
            workGroupToSysUserGR.initialize();
            workGroupToSysUserGR.setValue('user', current.getValue('user'));
            workGroupToSysUserGR.setValue('granted_by', current.getValue('group'));
            workGroupToSysUserGR.setValue('work_group', workGroup_Array[x].toString());
            workGroupToSysUserGR.setValue('inherit', true);
            workGroupToSysUserGR.insert();
        }
    }


})(current, previous);

