script.value:
(function executeRule(current, previous /*null when async*/) {

	GetUniqueArray = function(arr) {
		var uniqueArr = [...new Set(arr)];
		return uniqueArr;
	};

	//-----------------------------Get all segments that are tied to current work group----------------------------------------------------------
	let segmentsFromWorkGroup = [];
	var workGroupToSegment = new GlideRecord("x_cadso_work_group_segment_m2m");
	workGroupToSegment.addEncodedQuery('work_group=' + current.getValue('work_group'));
	workGroupToSegment.query();
	while(workGroupToSegment.next()) {
		segmentsFromWorkGroup.push(workGroupToSegment.getValue('segment'));
	}	
	segmentsFromWorkGroup = GetUniqueArray(segmentsFromWorkGroup);

	//-----------------------------For each segment, Create a Work USer to Segment m2m record----------------------------------------------------
	for(var x = 0; x < segmentsFromWorkGroup.length; x++) {
		if(segmentsFromWorkGroup[x] != '') {
			var workUserToSegment = new GlideRecord("x_cadso_work_user_segment_m2m");
			workUserToSegment.initialize();
			workUserToSegment.work_user = current.getValue('work_user');
			workUserToSegment.segment = segmentsFromWorkGroup[x].toString();
			workUserToSegment.inherit = true;
			workUserToSegment.granted_by = current.getUniqueValue();
			workUserToSegment.insert();
		}
	}
})(current, previous);

